@tailwind base;
@tailwind components;
@tailwind utilities;

/* MOBILE ORIENTATION LOCK - PREVENT ROTATION */
@media screen and (max-width: 768px) {
  html {
    -webkit-text-size-adjust: none;
    -ms-text-size-adjust: none;
    text-size-adjust: none;
    overflow: hidden;
    height: 100%;
  }

  body {
    -webkit-user-select: none;
    -webkit-touch-callout: none;
    -webkit-tap-highlight-color: transparent;
    overscroll-behavior: none;
    position: fixed;
    overflow: hidden;
    width: 100vw;
    height: 100vh;
    height: 100dvh; /* Dynamic viewport height for mobile */
  }

  #root {
    width: 100%;
    height: 100%;
    overflow-y: auto;
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch;
  }
}

/* Force portrait orientation styles */
@media screen and (orientation: landscape) and (max-width: 768px) {
  body::before {
    content: "Please rotate your device to portrait mode";
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    text-align: center;
    z-index: 9999;
    padding: 2rem;
  }
}

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 99%;

  --foreground: 220 20% 20%;

  --border: 220 15% 94%;

  --card: 0 0% 100%;

  --card-foreground: 220 20% 20%;

  --card-border: 220 15% 95%;

  --sidebar: 220 8% 98%;

  --sidebar-foreground: 220 20% 20%;

  --sidebar-border: 220 15% 94%;

  --sidebar-primary: 220 100% 50%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 220 10% 96%;

  --sidebar-accent-foreground: 220 20% 25%;

  --sidebar-ring: 220 100% 50%;

  --popover: 0 0% 100%;

  --popover-foreground: 220 20% 20%;

  --popover-border: 220 15% 92%;

  --primary: 220 100% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 220 8% 96%;

  --secondary-foreground: 220 20% 25%;

  --muted: 220 8% 97%;

  --muted-foreground: 220 20% 45%;

  --accent: 220 12% 96%;

  --accent-foreground: 220 20% 25%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 94%;
  --ring: 220 85% 25%;
  --chart-1: 220 85% 25%;
  --chart-2: 142 76% 36%;
  --chart-3: 38 92% 50%;
  --chart-4: 271 81% 56%;
  --chart-5: 262 83% 58%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: JetBrains Mono, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px hsl(220 43% 11% / 0.05);
  --shadow-xs: 0px 1px 2px 0px hsl(220 43% 11% / 0.05);
  --shadow-sm: 0px 1px 3px 0px hsl(220 43% 11% / 0.1), 0px 1px 2px -1px hsl(220 43% 11% / 0.1);
  --shadow: 0px 1px 3px 0px hsl(220 43% 11% / 0.1), 0px 1px 2px -1px hsl(220 43% 11% / 0.1);
  --shadow-md: 0px 4px 6px -1px hsl(220 43% 11% / 0.1), 0px 2px 4px -2px hsl(220 43% 11% / 0.1);
  --shadow-lg: 0px 10px 15px -3px hsl(220 43% 11% / 0.1), 0px 4px 6px -4px hsl(220 43% 11% / 0.1);
  --shadow-xl: 0px 20px 25px -5px hsl(220 43% 11% / 0.1), 0px 8px 10px -6px hsl(220 43% 11% / 0.1);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 43% 11% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 15% 8%;

  --foreground: 0 0% 95%;

  --border: 220 15% 15%;

  --card: 220 12% 12%;

  --card-foreground: 0 0% 95%;

  --card-border: 220 15% 18%;

  --sidebar: 220 12% 10%;

  --sidebar-foreground: 0 0% 95%;

  --sidebar-border: 220 15% 16%;

  --sidebar-primary: 220 85% 25%;

  --sidebar-primary-foreground: 0 0% 98%;

  --sidebar-accent: 220 10% 16%;

  --sidebar-accent-foreground: 0 0% 90%;

  --sidebar-ring: 220 85% 25%;

  --popover: 220 12% 12%;

  --popover-foreground: 0 0% 95%;

  --popover-border: 220 15% 18%;

  --primary: 220 85% 25%;

  --primary-foreground: 0 0% 98%;

  --secondary: 220 10% 16%;

  --secondary-foreground: 0 0% 90%;

  --muted: 220 10% 14%;

  --muted-foreground: 0 0% 65%;

  --accent: 220 10% 15%;

  --accent-foreground: 0 0% 90%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 20%;
  --ring: 220 85% 25%;
  --chart-1: 220 85% 45%;
  --chart-2: 142 76% 56%;
  --chart-3: 38 92% 70%;
  --chart-4: 271 81% 76%;
  --chart-5: 262 83% 78%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.1);
  --shadow-xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.1);
  --shadow-sm: 0px 1px 3px 0px hsl(0 0% 0% / 0.2), 0px 1px 2px -1px hsl(0 0% 0% / 0.2);
  --shadow: 0px 1px 3px 0px hsl(0 0% 0% / 0.2), 0px 1px 2px -1px hsl(0 0% 0% / 0.2);
  --shadow-md: 0px 4px 6px -1px hsl(0 0% 0% / 0.2), 0px 2px 4px -2px hsl(0 0% 0% / 0.2);
  --shadow-lg: 0px 10px 15px -3px hsl(0 0% 0% / 0.2), 0px 4px 6px -4px hsl(0 0% 0% / 0.2);
  --shadow-xl: 0px 20px 25px -5px hsl(0 0% 0% / 0.2), 0px 8px 10px -6px hsl(0 0% 0% / 0.2);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.35);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* Modern glassmorphism effects */
  .glass-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  }

  .dark .glass-card {
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  }

  /* Enhanced shadows */
  .modern-shadow {
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1), 0 4px 25px rgba(0, 0, 0, 0.07);
  }

  .dark .modern-shadow {
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.4), 0 4px 25px rgba(0, 0, 0, 0.2);
  }

  /* Gradient backgrounds */
  .gradient-bg {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary)) 100%);
  }

  .gradient-card {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%);
  }

  .dark .gradient-card {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.05) 0%, rgba(255, 255, 255, 0.02) 100%);
  }

  /* Interactive animations */
  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-pulse-slow {
    animation: pulse 4s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-scale-up {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .animate-scale-up:hover {
    transform: scale(1.02);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15);
  }

  .dark .animate-scale-up:hover {
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
  }

  /* Modern borders */
  .border-gradient {
    border: 1px solid transparent;
    background-clip: padding-box;
    position: relative;
  }

  .border-gradient::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 1px;
    background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--secondary)));
    border-radius: inherit;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: xor;
    -webkit-mask-composite: xor;
    z-index: -1;
  }

  /* Neon glow effects */
  .neon-glow {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
    transition: box-shadow 0.3s ease;
  }

  .neon-glow:hover {
    box-shadow: 0 0 30px hsl(var(--primary) / 0.5);
  }

  /* Shimmer effect */
  .shimmer {
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  /* Enhanced card effects */
  .enhanced-card {
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dark .enhanced-card {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .enhanced-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }

  .dark .enhanced-card:hover {
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4);
  }

  /* Modern scrollbars */
  .modern-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .modern-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }

  .modern-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--muted));
    border-radius: 3px;
  }

  .modern-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--muted-foreground));
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

/* RTL Support */
.rtl {
