}

.mobile-pulse {
  animation: mobile-pulse 2s infinite;
}

.mobile-pulse-glow {
  animation: mobile-pulse-glow 2s infinite;
}

/* Beautiful Interactive Enhancements */
.interactive-bounce {
  animation: bounceIn 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.wave-animation {
  animation: wave 2s ease-in-out infinite;
}

.glow-effect {
  animation: glow 2s ease-in-out infinite alternate;
}

/* New Keyframes for Enhanced Animations */
@keyframes bounceIn {
  0% {
    opacity: 0;
    transform: scale(0.3) translateY(50px);
  }
  50% {
    opacity: 1;
    transform: scale(1.05) translateY(-10px);
  }
  70% {
    transform: scale(0.95) translateY(5px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateY(0px);
  }
}

@keyframes wave {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  }
  50% {
    box-shadow: 0 0 40px hsl(var(--primary) / 0.6), 0 0 60px hsl(var(--primary) / 0.3);
  }
}

@keyframes shimmer {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes float-particle {
  0% {
    transform: translateY(100vh) rotate(0deg);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100px) rotate(360deg);
    opacity: 0;
  }
}

/* Particle Animation Background Classes */
.particle-bg {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
}

.particle {
  position: absolute;
  width: 4px;
  height: 4px;
  background: hsl(var(--primary) / 0.3);
  border-radius: 50%;
  animation: float-particle 6s infinite linear;
  will-change: transform;
  pointer-events: none;
}

.particle:nth-child(1) { animation-delay: 0s; left: 10%; }
.particle:nth-child(2) { animation-delay: 1s; left: 20%; }
.particle:nth-child(3) { animation-delay: 2s; left: 30%; }
.particle:nth-child(4) { animation-delay: 3s; left: 40%; }
.particle:nth-child(5) { animation-delay: 4s; left: 50%; }

.animate-delay-100 {
  animation-delay: 100ms;
}

.animate-delay-200 {
  animation-delay: 200ms;
}

.animate-delay-300 {
  animation-delay: 300ms;
}

.animate-delay-400 {
  animation-delay: 400ms;
}

.animate-delay-500 {
  animation-delay: 500ms;
}

.animate-delay-600 {
  animation-delay: 600ms;
}

/* Morphing Button Animation */
.morph-button {
  position: relative;
  overflow: hidden;
  transform-style: preserve-3d;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.morph-button:hover {
  transform: translateY(-2px) rotateX(5deg);
}

.morph-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
  transition: left 0.5s ease;
  pointer-events: none;
}

.morph-button:hover::before {
  left: 100%;
}

/* Touch Feedback Effect */
.touch-feedback {
  position: relative;
  overflow: hidden;
}

.touch-feedback::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: hsl(var(--primary) / 0.3);
  transform: translate(-50%, -50%);
  transition: all 0.5s ease;
  pointer-events: none;
}

.touch-feedback:active::after {
  width: 200px;
  height: 200px;
  transition: all 0.2s ease;
}

/* Progress Bar Animation */
.progress-bar-animated {
  position: relative;
  overflow: hidden;
}

.progress-bar-animated::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background-image: linear-gradient(
    -45deg,
    rgba(255, 255, 255, 0.2) 25%,
    transparent 25%,
    transparent 50%,
    rgba(255, 255, 255, 0.2) 50%,
    rgba(255, 255, 255, 0.2) 75%,
    transparent 75%,
    transparent
  );
  background-size: 30px 30px;
  animation: progress-shimmer 2s infinite linear;
}

@keyframes progress-shimmer {
  0% { transform: translateX(-30px); }
  100% { transform: translateX(100%); }
}

/* Performance optimizations for reduced motion */
@media (prefers-reduced-motion: reduce) {
  .animate-pulse,
  .animate-bounce,
  .animate-spin,
  .animate-ping,
  .particle,
  .morph-button,
  .glow-effect,
  .mobile-pulse-glow,
  .wave-animation,
  .interactive-bounce,
  .progress-bar-animated {
    animation: none;
    transform: none;
    transition: none;
  }

  .particle {
    display: none;
  }

  .backdrop-blur-xl,
  .backdrop-blur-lg,
  .backdrop-blur-md {
    backdrop-filter: none;
  }
}

/* Mobile performance optimizations */
@media (max-width: 768px) and (pointer: coarse) {
  .backdrop-blur-xl {
    backdrop-filter: blur(8px);
  }
  .backdrop-blur-lg {
    backdrop-filter: blur(6px);
  }
  .backdrop-blur-md {
    backdrop-filter: blur(4px);
  }

  .shadow-2xl {
    box-shadow: 0 10px 25px -3px rgba(0, 0, 0, 0.1);
  }

  .shadow-xl {
    box-shadow: 0 8px 20px -2px rgba(0, 0, 0, 0.08);
  }
}

