}

@keyframes mobile-pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
    transform: scale(1);
  }
  50% {
    box-shadow: 0 0 30px hsl(var(--primary) / 0.5);
    transform: scale(1.02);
  }
}

.interactive-bounce {
  transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.interactive-bounce:active {
  transform: scale(0.95);
}

.wave-animation {
  position: relative;
  overflow: hidden;
}

.wave-animation::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, hsl(var(--primary) / 0.2), transparent);
  animation: wave-move 2s infinite;
}

@keyframes wave-move {
  0% { left: -100%; }
  100% { left: 100%; }
}

.mobile-scale-in {
  animation: mobile-scale-in 0.3s ease-out;
}

@keyframes mobile-scale-in {
  0% {
    opacity: 0;
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

.mobile-fade-in-up {
  animation: mobile-fade-in-up 0.4s ease-out;
}

@keyframes mobile-fade-in-up {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.glow-effect {
  position: relative;
  box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  will-change: box-shadow;
}

.glow-effect:hover {
  box-shadow: 0 0 30px hsl(var(--primary) / 0.5);
}

/* Enhanced Mobile Background */
.mobile-enhanced {
  background: linear-gradient(135deg, 
    hsl(var(--background)) 0%, 
    hsl(220 15% 99%) 25%,
    hsl(var(--muted)) 100%);
  min-height: 100vh;
}

/* Enhanced Mobile Cards - Beautiful & Interactive */
.mobile-grid .mobile-card {
  transform: translateY(0);
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.98), rgba(255, 255, 255, 0.92));
  backdrop-filter: blur(12px);
  border: 1px solid hsl(var(--primary) / 0.1);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.05);
  position: relative;
  overflow: hidden;
}

.mobile-grid .mobile-card::before {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  background: linear-gradient(45deg, 
    transparent, 
    hsl(var(--primary) / 0.1), 
    transparent, 
    hsl(var(--accent) / 0.1), 
    transparent
  );
  border-radius: inherit;
  z-index: -1;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.mobile-grid .mobile-card:hover::before {
  opacity: 1;
  animation: shimmer 2s infinite;
}

.mobile-grid .mobile-card:hover {
  transform: translateY(-6px) rotate(1deg);
  box-shadow: 0 12px 40px rgba(0, 0, 0, 0.12);
}

.mobile-card {
  border: 1px solid hsl(var(--border));
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.03);
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(255, 255, 255, 0.9));
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
}

.mobile-card::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: radial-gradient(circle, hsl(var(--primary) / 0.2) 0%, transparent 70%);
  transform: translate(-50%, -50%);
  transition: all 0.4s ease;
  pointer-events: none;
}

.mobile-card:hover {
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
  border-color: hsl(var(--primary) / 0.3);
  transform: translateY(-4px) scale(1.02);
}

.mobile-card:active::after {
  width: 300px;
  height: 300px;
}

/* Enhanced Interactive Elements */
.mobile-interactive {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.mobile-interactive:hover {
  transform: scale(1.02);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
}

.mobile-interactive:active {
  transform: scale(0.98);
}

/* Glass effect - Optimized */
.mobile-glass {
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(8px);
  border: 1px solid rgba(255, 255, 255, 0.3);
}

/* Enhanced Property Cards */
.property-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.property-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.6s;
}

.property-card:hover::before {
  left: 100%;
}

.property-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
}

.mobile-button {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  transform: translateZ(0);
}

.mobile-button::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  background: rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  transition: width 0.3s, height 0.3s;
}

.mobile-button:hover::before {
  width: 300px;
  height: 300px;
}

.mobile-button:active {
  transform: scale(0.95) translateZ(0);
  transition: all 0.1s ease-out;
}

/* Enhanced Mobile Navigation */
.mobile-nav-item {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.mobile-nav-item::before {
  content: '';
  position: absolute;
  top: 0;
  left: 50%;
  width: 0;
  height: 2px;
  background: hsl(var(--primary));
  transition: all 0.3s ease;
  transform: translateX(-50%);
}

.mobile-nav-item.active::before {
  width: 80%;
}

.mobile-nav-item:active {
  transform: scale(0.95);
}

/* Enhanced Property Cards */
.property-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.property-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
  transition: left 0.5s ease;
}

.property-card:hover::before {
  left: 100%;
}

.property-card:active {
  transform: scale(0.98);
}

/* Staggered Animation Delays */
.animate-delay-100 {
  animation-delay: 0.1s;
}

.animate-delay-200 {
  animation-delay: 0.2s;
}

.animate-delay-300 {
  animation-delay: 0.3s;
}

.animate-delay-400 {
  animation-delay: 0.4s;
}

/* Mobile Responsive Enhancements */
@media (max-width: 768px) {
  .mobile-enhanced {
    padding: 1rem;
  }

  .mobile-grid {
    gap: 0.75rem;
  }

  .mobile-text {
    font-size: 0.875rem;
    line-height: 1.25rem;
  }

  .mobile-button-large {
    padding: 0.75rem 1.5rem;
    font-size: 1rem;
    min-height: 3rem;
  }
}

/* Touch-friendly Interactive Areas */
.touch-target {
  min-height: 44px;
  min-width: 44px;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Glass Morphism Effect for Mobile */
.glass-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.dark .glass-card {
  background: rgba(0, 0, 0, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* RTL language-specific text direction for specific elements */
.rtl .language-text {
  direction: rtl;
  text-align: right;
}

.rtl .number-text {
  direction: ltr;
  text-align: left;
}

/* Currency display always LTR */
.currency {
  direction: ltr;
  display: inline-block;
}

.rtl .currency {
  direction: ltr;
}

/* Mobile specific RTL adjustments */
@media (max-width: 768px) {
  .rtl .mobile-card {
    text-align: right;
  }

  .rtl .mobile-flex {
    flex-direction: row-reverse;
  }

  .rtl .mobile-grid {
    direction: rtl;
  }
}

