                </div>
              </CardContent>
            </motion.div>
          )}
        </AnimatePresence>
      </Card>
    </motion.div>
  )
}

client/src/components/dashboard-chart.ts.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Bar, BarChart, Line, LineChart, ResponsiveContainer, XAxis, YAxis, CartesianGrid, Tooltip } from "recharts"

interface ChartData {
  name: string
  value: number
  [key: string]: any
}

interface DashboardChartProps {
  title: string
  description?: string
  data: ChartData[]
  type: "bar" | "line"
  dataKey?: string
}

export function DashboardChart({ 
  title, 
  description, 
  data, 
  type, 
  dataKey = "value" 
}: DashboardChartProps) {
  return (
    <Card 
      className="enhanced-card animate-scale-up relative overflow-hidden group" 
      data-testid={`card-chart-${title.toLowerCase().replace(/\s+/g, '-')}`}
    >
      <div className="absolute inset-0 gradient-card opacity-50" />
      <CardHeader className="relative z-10">
        <CardTitle className="text-lg font-semibold bg-gradient-to-r from-foreground to-primary/80 bg-clip-text text-transparent">
          {title}
        </CardTitle>
        {description && (
          <CardDescription className="text-muted-foreground/80">{description}</CardDescription>
        )}
      </CardHeader>
      <CardContent className="relative z-10">
        <div className="p-2 rounded-lg bg-background/50 backdrop-blur-sm">
          <ResponsiveContainer width="100%" height={300}>
            {type === "bar" ? (
              <BarChart data={data}>
                <defs>
                  <linearGradient id="colorGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="hsl(var(--primary))" stopOpacity={0.8}/>
                    <stop offset="95%" stopColor="hsl(var(--primary))" stopOpacity={0.3}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" className="opacity-20" stroke="hsl(var(--muted-foreground))" />
                <XAxis 
                  dataKey="name" 
                  tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
                  axisLine={false}
                  tickLine={false}
                />
                <YAxis 
                  tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
                  axisLine={false}
                  tickLine={false}
                />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: 'hsl(var(--background))',
                    border: '1px solid hsl(var(--border))',
                    borderRadius: '12px',
                    boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)',
                    backdropFilter: 'blur(20px)'
                  }}
                />
                <Bar 
                  dataKey={dataKey} 
                  fill="url(#colorGradient)" 
                  radius={[6, 6, 0, 0]}
                />
              </BarChart>
            ) : (
              <LineChart data={data}>
                <defs>
                  <linearGradient id="lineGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="hsl(var(--primary))" stopOpacity={0.3}/>
                    <stop offset="95%" stopColor="hsl(var(--primary))" stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" className="opacity-20" stroke="hsl(var(--muted-foreground))" />
                <XAxis 
                  dataKey="name" 
                  tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
                  axisLine={false}
                  tickLine={false}
                />
                <YAxis 
                  tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
                  axisLine={false}
                  tickLine={false}
                />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: 'hsl(var(--background))',
                    border: '1px solid hsl(var(--border))',
                    borderRadius: '12px',
                    boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)',
                    backdropFilter: 'blur(20px)'
                  }}
                />
                <Line 
                  type="monotone" 
                  dataKey={dataKey} 
                  stroke="hsl(var(--primary))" 
                  strokeWidth={3}
                  dot={{ fill: 'hsl(var(--primary))', strokeWidth: 3, r: 6, className: 'animate-pulse' }}
                  activeDot={{ r: 8, className: 'animate-pulse' }}
                  fill="url(#lineGradient)"
                />
              </LineChart>
            )}
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  )
}

client/src/components/document-card.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { FileText, Download, Eye, Trash2 } from "lucide-react"
import { KycDocument } from "@shared/schema"
import { format } from "date-fns"

interface DocumentCardProps {
  document: KycDocument
  onView?: (id: string) => void
  onDownload?: (id: string) => void
  onDelete?: (id: string) => void
}

export function DocumentCard({ document, onView, onDownload, onDelete }: DocumentCardProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case "approved":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
      case "rejected":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
      default:
        return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
    }
  }

  const getDocumentTypeLabel = (type: string) => {
    switch (type) {
      case "national_id":
        return "National ID"
      case "iqama":
        return "Iqama"
      case "passport":
        return "Passport"
      case "selfie":
        return "Selfie Photo"
      case "proof_of_income":
        return "Proof of Income"
      case "address_proof":
        return "Address Proof"
      case "employment_letter":
        return "Employment Letter"
      default:
        return type.replace('_', ' ').toUpperCase()
    }
  }

  const handleView = () => {
    console.log('View document triggered:', document.id)
    onView?.(document.id)
  }

  const handleDownload = () => {
    console.log('Download document triggered:', document.id)
    onDownload?.(document.id)
  }

  const handleDelete = () => {
    console.log('Delete document triggered:', document.id)
    onDelete?.(document.id)
  }

  return (
    <Card className="hover-elevate" data-testid={`card-document-${document.id}`}>
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <div className="flex items-center gap-2">
          <FileText className="h-4 w-4 text-muted-foreground" />
          <CardTitle className="text-sm" data-testid={`text-type-${document.id}`}>
            {getDocumentTypeLabel(document.documentType)}
          </CardTitle>
        </div>
        <Badge 
          className={getStatusColor(document.status)}
          data-testid={`badge-status-${document.id}`}
        >
          {document.status}
        </Badge>
      </CardHeader>
      <CardContent className="space-y-3">
        <div className="text-xs text-muted-foreground" data-testid={`text-date-${document.id}`}>
          Uploaded {format(new Date(document.uploadedAt!), 'MMM dd, yyyy')}
        </div>

        {document.reviewNotes && (
          <div className="text-xs text-muted-foreground bg-muted p-2 rounded" data-testid={`text-notes-${document.id}`}>
            <strong>Notes:</strong> {document.reviewNotes}
          </div>
        )}

        <div className="flex gap-2">
          <Button
            variant="outline"
            size="sm"
            onClick={handleView}
            data-testid={`button-view-${document.id}`}
            className="flex-1"
          >
            <Eye className="h-3 w-3 mr-1" />
            View
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={handleDownload}
            data-testid={`button-download-${document.id}`}
            className="flex-1"
          >
            <Download className="h-3 w-3 mr-1" />
            Download
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={handleDelete}
            data-testid={`button-delete-${document.id}`}
          >
            <Trash2 className="h-3 w-3" />
          </Button>
        </div>
      </CardContent>
    </Card>
  )
}

client/src/components/investor-table.tsx.content:
import { useState } from "react"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Avatar, AvatarFallback } from "@/components/ui/avatar"
import { CheckCircle, XCircle, Clock } from "lucide-react"
import { Investor } from "@shared/schema"

interface InvestorTableProps {
  investors: Investor[]
  onApproveKyc?: (investorId: string) => void
  onRejectKyc?: (investorId: string) => void
}

export function InvestorTable({ investors, onApproveKyc, onRejectKyc }: InvestorTableProps) {
  const [processing, setProcessing] = useState<string | null>(null)

  const getKycStatusColor = (status: string) => {
    switch (status) {
      case "approved":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
      case "rejected":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
      default:
        return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
    }
  }

  const getKycIcon = (status: string) => {
    switch (status) {
      case "approved":
        return <CheckCircle className="h-4 w-4" />
      case "rejected":
        return <XCircle className="h-4 w-4" />
      default:
        return <Clock className="h-4 w-4" />
    }
  }

  const handleApprove = async (investorId: string) => {
    setProcessing(investorId)
    console.log('Approve KYC triggered:', investorId)
    await new Promise(resolve => setTimeout(resolve, 1000)) // Simulate API call
    onApproveKyc?.(investorId)
    setProcessing(null)
  }

  const handleReject = async (investorId: string) => {
    setProcessing(investorId)
    console.log('Reject KYC triggered:', investorId)
    await new Promise(resolve => setTimeout(resolve, 1000)) // Simulate API call
    onRejectKyc?.(investorId)
    setProcessing(null)
  }

  return (
    <div className="rounded-md border" data-testid="table-investors">
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Investor</TableHead>
            <TableHead>Contact</TableHead>
            <TableHead>KYC Status</TableHead>
            <TableHead>Total Invested</TableHead>
            <TableHead>Properties</TableHead>
            <TableHead>Monthly Income</TableHead>
            <TableHead>Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {investors.map((investor) => (
            <TableRow key={investor.id} data-testid={`row-investor-${investor.id}`}>
              <TableCell className="flex items-center gap-3">
                <Avatar>
                  <AvatarFallback>
                    {investor.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <div className="font-medium" data-testid={`text-name-${investor.id}`}>
                    {investor.name}
                  </div>
                  <div className="text-sm text-muted-foreground" data-testid={`text-nationality-${investor.id}`}>
                    {investor.nationality}
                  </div>
                </div>
              </TableCell>
              <TableCell>
                <div className="text-sm">
                  <div data-testid={`text-email-${investor.id}`}>{investor.email}</div>
                  {investor.phone && (
                    <div className="text-muted-foreground" data-testid={`text-phone-${investor.id}`}>
                      {investor.phone}
                    </div>
                  )}
                </div>
              </TableCell>
              <TableCell>
                <Badge 
                  className={`${getKycStatusColor(investor.kycStatus)} flex items-center gap-1 w-fit`}
                  data-testid={`badge-kyc-${investor.id}`}
                >
                  {getKycIcon(investor.kycStatus)}
                  {investor.kycStatus}
                </Badge>
              </TableCell>
              <TableCell className="font-mono" data-testid={`text-invested-${investor.id}`}>
                SAR {Number(investor.totalInvested).toLocaleString()}
              </TableCell>
              <TableCell data-testid={`text-properties-${investor.id}`}>
                {investor.activeProperties}
              </TableCell>
              <TableCell className="font-mono" data-testid={`text-income-${investor.id}`}>
                SAR {Number(investor.monthlyIncome).toLocaleString()}
              </TableCell>
              <TableCell>
                {investor.kycStatus === "pending" && (
                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      variant="default"
                      onClick={() => handleApprove(investor.id)}
                      disabled={processing === investor.id}
                      data-testid={`button-approve-${investor.id}`}
                    >
                      {processing === investor.id ? "..." : "Approve"}
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => handleReject(investor.id)}
                      disabled={processing === investor.id}
                      data-testid={`button-reject-${investor.id}`}
                    >
                      {processing === investor.id ? "..." : "Reject"}
                    </Button>
                  </div>
                )}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  )
}

client/src/components/notification-card..content:
import { Card, CardContent, CardHeader } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Bell, CheckCircle, AlertTriangle, Info, X } from "lucide-react"
import { Notification } from "@shared/schema"
import { format } from "date-fns"

interface NotificationCardProps {
  notification: Notification
  onMarkAsRead?: (id: string) => void
  onDelete?: (id: string) => void
}

export function NotificationCard({ notification, onMarkAsRead, onDelete }: NotificationCardProps) {
  const getTypeIcon = (type: string) => {
    switch (type) {
      case "success":
        return <CheckCircle className="h-4 w-4 text-green-600" />
      case "warning":
        return <AlertTriangle className="h-4 w-4 text-yellow-600" />
      case "error":
        return <X className="h-4 w-4 text-red-600" />
      default:
        return <Info className="h-4 w-4 text-blue-600" />
    }
  }

  const getTypeColor = (type: string) => {
    switch (type) {
      case "success":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
      case "warning":
        return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
      case "error":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
      default:
        return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
    }
  }

  const handleMarkAsRead = () => {
    console.log('Mark as read triggered:', notification.id)
    onMarkAsRead?.(notification.id)
  }

  const handleDelete = () => {
    console.log('Delete notification triggered:', notification.id)
    onDelete?.(notification.id)
  }

  return (
    <Card 
      className={`${notification.isRead ? 'opacity-70' : ''} hover-elevate`}
      data-testid={`card-notification-${notification.id}`}
    >
      <CardHeader className="flex flex-row items-start justify-between space-y-0 pb-2">
        <div className="flex items-center gap-2">
          {getTypeIcon(notification.type)}
          <h4 className="font-semibold" data-testid={`text-title-${notification.id}`}>
            {notification.title}
          </h4>
          {!notification.isRead && (
            <Badge variant="secondary" className="text-xs">New</Badge>
          )}
        </div>
        <div className="flex items-center gap-1">
          <Badge 
            className={`${getTypeColor(notification.type)} text-xs`}
            data-testid={`badge-type-${notification.id}`}
          >
            {notification.type}
          </Badge>
          <Button
            variant="ghost"
            size="icon"
            onClick={handleDelete}
            data-testid={`button-delete-${notification.id}`}
            className="h-6 w-6"
          >
            <X className="h-3 w-3" />
          </Button>
        </div>
      </CardHeader>
      <CardContent className="space-y-3">
        <p className="text-sm text-muted-foreground" data-testid={`text-message-${notification.id}`}>
          {notification.message}
        </p>
        <div className="flex items-center justify-between">
          <span className="text-xs text-muted-foreground" data-testid={`text-date-${notification.id}`}>
            {format(new Date(notification.createdAt!), 'MMM dd, yyyy HH:mm')}
          </span>
          {!notification.isRead && (
            <Button
              variant="outline"
              size="sm"
              onClick={handleMarkAsRead}
              data-testid={`button-mark-read-${notification.id}`}
            >
              Mark as Read
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
